    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
        try {
            con = db.start();
            ps = con.prepareStatement("select * from equipments_new where Asset_Tag='"+asset_tag.getText()+"' or Serial_No='"+serial_no.getText()+"'");
            rs =ps.executeQuery();
            if(rs.next()){
                JOptionPane.showMessageDialog(null, "Equipment with that serial number exits");
            }else{
            ps = con.prepareStatement("insert into equipments_new(Asset_Tag,Asset_Type,Manufacturer,Model,Serial_No,Site,Location,Department, Asset_Keeper, status, Purchase_Cost,Purchase_Date) values(?,?,?,?,?,?,?,?,?,?,?,?)");
            ps.setString(1,  asset_tag.getText());
            ps.setString(2, (String) asset_type.getSelectedItem());
            ps.setString(3, (String) manufacturer.getSelectedItem());
            ps.setString(4, (String) model.getSelectedItem());
            ps.setString(5, serial_no.getText());
            ps.setString(6,(String) site.getSelectedItem());
            ps.setString(7, (String) location.getSelectedItem());
            ps.setString(8, (String) department.getSelectedItem());
            ps.setString(9, asset_keeper.getText());
            ps.setString(10, (String) status.getSelectedItem());
            ps.setString(11, purchase_cost.getText());
            SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
            String dt = sdf.format(purchase_date.getDate());
            ps.setString(12, dt);
            ps.executeUpdate();
            JOptionPane.showMessageDialog(null, "Equipment Added Successfully");
            }
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(null, ex.getMessage());
        }
    }//GEN-LAST:event_addActionPerformed
