  public void update_date(){
         try {
             con = db.start();
             ps = con.prepareStatement("select id,due_date from ppm_schedule");
             rs = ps.executeQuery();
            while(rs.next()){
            SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
             String id = rs.getString("id");
             //System.out.println(id);
             Date dat = rs.getDate("due_date");
             //System.out.println(sdf.format(dat));
             java.util.Date date = new java.util.Date();            
             java.sql.Date dat1 = new  java.sql.Date(date.getTime());
             //System.out.println(sdf.format(dat1));
             
             long diff = dat.getTime() - dat1.getTime();
             //System.out.println(TimeUnit.DAYS.convert(diff, TimeUnit.MILLISECONDS));
             String var = String.valueOf(TimeUnit.DAYS.convert(diff, TimeUnit.MILLISECONDS));

             ps = con.prepareStatement("update ppm_schedule set due_in_days=? where id='"+id+"'");
             ps.setString(1, var);
             ps.execute();
            }
           
         } catch (SQLException ex) {
             Logger.getLogger(Esoft.class.getName()).log(Level.SEVERE, null, ex);
         }
  }
