    private void serviceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_serviceActionPerformed
        try{
        if(jComboBox1.getSelectedIndex()!=0 && jComboBox2.getSelectedIndex()!=0){
         jDateChooser1.setDate(null);
         jDateChooser3.setDate(null);
        
            con = db.start();
            ps = con.prepareStatement("select * from preventive_maintenance where site='"+jComboBox1.getSelectedItem()+"' and location='"+jComboBox2.getSelectedItem()+"'");
            rs = ps.executeQuery();
            while(rs.next()){
             jDateChooser1.setDate(rs.getDate("date_done"));
             SimpleDateFormat sdf = new SimpleDateFormat("MMM d, yyyy");
             java.util.Date d = jDateChooser1.getDate();
             java.sql.Date sqldate = new java.sql.Date(d.getTime());
             
             Calendar c = Calendar.getInstance();
             c.setTime(sqldate);
             c.add(Calendar.MONTH, 6);
             
             //Date newDate = (Date) c.getTime();
             java.util.Date newDate = new java.util.Date(sdf.format(c.getTime()));
             jDateChooser3.setDate(newDate);
             
             java.util.Date date = new java.util.Date();
             
             
             String inputString1 = sdf.format(date);
             String inputString2 = sdf.format(newDate);
             
             java.util.Date date1 = sdf.parse(inputString1);
             java.util.Date date2 = sdf.parse(inputString2);
             
                   
             long diff = date2.getTime() - date1.getTime();
             String var = String.valueOf(TimeUnit.DAYS.convert(diff, TimeUnit.MILLISECONDS));
             jTextField2.setText(var);             
            }        
        }
        else if(jComboBox1.getSelectedIndex()!=0 && jComboBox2.getSelectedIndex()==0){
            ps = con.prepareStatement("select * from preventive_maintenance where site='"+jComboBox1.getSelectedItem()+"'");
            rs = ps.executeQuery();
            while(rs.next()){
             jDateChooser1.setDate(rs.getDate("date_done"));
             SimpleDateFormat sdf = new SimpleDateFormat("MMM d, yyyy");
             java.util.Date d = jDateChooser1.getDate();
             java.sql.Date sqldate = new java.sql.Date(d.getTime());
             
             Calendar c = Calendar.getInstance();
             c.setTime(sqldate);
             c.add(Calendar.MONTH, 6);
             
             //Date newDate = (Date) c.getTime();
             java.util.Date newDate = new java.util.Date(sdf.format(c.getTime()));
             jDateChooser3.setDate(newDate);
             
             java.util.Date date = new java.util.Date();
             
             
             String inputString1 = sdf.format(date);
             String inputString2 = sdf.format(newDate);
             
             java.util.Date date1 = sdf.parse(inputString1);
             java.util.Date date2 = sdf.parse(inputString2);
             
                   
             long diff = date2.getTime() - date1.getTime();
             String var = String.valueOf(TimeUnit.DAYS.convert(diff, TimeUnit.MILLISECONDS));
             jTextField2.setText(var);             
            }
        }else{
            JOptionPane.showMessageDialog(null, "Select Site Or Location");
        }
        }catch (ParseException | SQLException ex) {
             JOptionPane.showMessageDialog(null,ex.getMessage());
         }
    }//GEN-LAST:event_serviceActionPerformed

